mapper.select:
SELECT * FROM
   ( SELECT rownum rnum,
    M.*,  us.userid as "senderid" FROM (
      SELECT
         u.userid, m.msno, m.msuno, m.msreceiver, m.mstitle, m.mscontent, m.mswrittentime, m.mstype                        
      FROM tb_message m, tb_user u
      WHERE m.msowner = #{uno}
         AND m.mstype= 0
         AND u.uno = #{uno}
         ORDER BY m.msno DESC
      ) M, tb_user us where M.msuno = us.uno
   ) MR
WHERE rnum BETWEEN #{paging.start} and #{paging.end}

   SELECT * FROM
   ( SELECT rownum rnum,
    M.*,  us.userid as "receiverid" FROM (
      SELECT
         u.userid, m.msno, m.msuno, m.msreceiver, m.mstitle, m.mscontent, m.mswrittentime, m.mstype                        
      FROM tb_message m, tb_user u
      WHERE m.msowner = #{uno}
         AND m.mstype= 1
         AND u.uno = #{uno}
         ORDER BY m.msno DESC
      ) M, tb_user us where M.msreceiver = us.uno
   ) MR
WHERE rnum BETWEEN #{paging.start} and #{paging.end}

SELECT * FROM
   ( SELECT rownum rnum,
    M.*,  us.userid as "senderid" FROM (
      SELECT
         u.userid, m.msno, m.msuno, m.msreceiver, m.mstitle, m.mscontent, m.mswrittentime, m.mstype                        
      FROM tb_message m, tb_user u
      WHERE m.msowner = #{uno}
         AND m.mstype= 3
         AND u.uno = #{uno}
         ORDER BY m.msno DESC
      ) M, tb_user us where M.msuno = us.uno
   ) MR
WHERE rnum BETWEEN #{paging.start} and #{paging.end}

SELECT * FROM
      ( SELECT rownum rnum,
       M.*,  us.userid as "senderid" FROM (
         SELECT
            u.userid, m.msno, m.msuno, m.msreceiver, m.mstitle, m.mscontent, m.mswrittentime, m.mstype                        
         FROM tb_message m, tb_user u
         WHERE m.msowner = #{uno}
            AND m.mstype= 2
            AND u.uno = #{uno}
            ORDER BY m.msno DESC
         ) M, tb_user us where M.msuno = us.uno
      ) MR
   WHERE rnum BETWEEN #{paging.start} and #{paging.end}
